import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';
import { Button } from './Button';

function Navbar() {
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true)

    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false)

    const showButton = () => {
      if (window.innerWidth <= 960) {
        setButton(false);
      } else {
        setButton(true);
      }
    };

    window.addEventListener('resize', showButton);

  return (
    <>
      <nav className='navbar'>
          <div className='navbar-container'>
              <Link to='/' className='navbar-logo'>
                  IELTS AT HOME <i className='fab fa-typo3' />
              </Link>
              <div className='menu-icon' onClick={handleClick}>
                  <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
              </div>
              <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                  <li className='nav-item'>
                    <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                        Home
                    </Link>
                  </li>
                  <li className='nav-item'>
                    <Link to='/speaking' className='nav-links' onClick={closeMobileMenu}>
                        Speaking
                    </Link>
                  </li>
                  <li className='nav-item'>
                    <Link to='/listening' className='nav-links' onClick={closeMobileMenu}>
                        Listening
                    </Link>
                  </li>
                  <li className='nav-item'>
                    <Link to='/reading' className='nav-links' onClick={closeMobileMenu}>
                        Reading
                    </Link>
                  </li>
                  <li className='nav-item'>
                    <Link to='/writing' className='nav-links' onClick={closeMobileMenu}>
                        Writing
                    </Link>
                  </li>
                  <li className='nav-item'>
                    <Link to='/sign-up' className='nav-links-moblie' onClick={closeMobileMenu}>
                        Sign Up
                    </Link>
                  </li>
              </ul>
              {button && <Button buttonStyle='btn--outline'>SIGN UP</Button>}
          </div>
      </nav>
    </>
  );
}

export default Navbar;
